const Icon = ({ image, name, size , role, link }) => (

    <div> 
        <p> role =  </p>
        <a link={link} target="_blank" rel="noopener noreferrer">
        <img
        // Yep, this size calculation for smaller screens is hacky
        className={`w-${Math.max(
            size - 24,
            24
        )} md:w-${size} hover:opacity-75 md:mx-3 md:my-1`}
        src={image}
        alt={role}
        />
        </a>
    </div>


  );


  export default Icon;
