import React from "react";
import "../style/eboardicon.css"
import linkdenIcon from "../images/linkdenIcon.png";

const EboardIcon = ({name, image, title, description}) => (

    <div className= "Eboard-item"> 
        <a className="">
        <img style={{width: "100%", height: "100%"}} src={image}/> 
        </a>
        <p className="Ebaord-name"> {name} </p>
        <p className="Eboard-title"> {title} </p>
        <a className= "Eboard-linkden" href="https://www.linkedin.com/in/emma-foresi/" target="_blank" rel="noopener noreferrer">
        <img style= {{width: "2%", height: "2%"}} src={linkdenIcon} />
        </a>
        <p className="Eboard-description">
            {description}
        </p>
    </div>
  );


  export default EboardIcon;
